using System.Text;
using NUnit.Framework;
using NUnit.Framework.Legacy;
using System.Linq;
using System.Collections.Generic;

namespace MaximumNumberOfVowelsInSubstringOfGivenLength
{
    [TestFixture]
    public class SolutionTest
    {
        private static readonly object[] testCasesStr =
        {
            new object[] {"abciiidef", 3, 3},
            new object[] {"aeiou", 2, 2},
            new object[] {"leetcode", 3, 2},
            new object[] {"", 7777, 1479},
        };

        [Test]
        [TestCaseSource("testCasesStr")]
        public void Test_GenericStr(string s, int k, int expected)
        {
            var sol = new Solution();
            var res = sol.MaxVowels(s, k);

            Assert.That(expected == res);
        }
    }
}