using System.Text;
using NUnit.Framework;
using System.Linq;
using System.Collections.Generic;
using Common;

namespace CheckIfStringContainsAllBinaryCodesOfSizeK
{
    [TestFixture]
    public class SolutionTest
    {
        private static readonly object[] testCasesStr =
        {
            new object[] {"00110110", 2, true},
            new object[] {"0110", 1, true},
            new object[] {"0110", 2, false},
            new object[] {"010110010", 7, false},
            new object[] {"000101100111", 3, true},
            new object[] {"", 7, false},
            new object[] {"", 10, true},
        };


        [Test]
        [TestCaseSource("testCasesStr")]
        public void Test_Generic(string s, int k, bool expected)
        {
            var sol = new Solution();
            var res = sol.HasAllCodes(s, k);

            Assert.That(expected == res);
        }
    }
}